"use strict";

const deps = { Util: require('Util.js') };

/**
 * A base object for all of SteerGlance
 *
 * @class BJOObject
 * @constructor
 *
 * historically named BJO (ButlerJamesObject)... TODO should be renamed
 */
function BJOObject() {
    this._guid = deps.Util.guid(); // this is the OBJECT's guid; upon clone()ing the object, the new object should receive a new guid!
}

/**
 * external constructor
 * @function create
 * @static
 */
function create() {
    return new BJOObject();
}

BJOObject.prototype = {};
BJOObject.prototype.constructor = BJOObject;
BJOObject.prototype._guid = null;

exports.BJOObject = BJOObject;
exports.create = create;

BJOObject.prototype.getGuid = function() {
    return this._guid;
};
