var Util = require('Util.js');

function BJOObject() {
    this._guid = Util.guid(); // this is the OBJECT's guid; upon clone()ing the object, the new object should receive a new guid!
}

function create() {
    return new BJOObject();
}

BJOObject.prototype = {};
BJOObject.prototype.constructor = BJOObject;

BJOObject.prototype.guid = function() {
    return this._guid;
};

exports.BJOObject = BJOObject;
exports.create = create;
