"use strict";

const _ = require('lodash');

/**
 * delegate to all functions in the *to* class.
 * i.e., from.foo() will really call to.foo()
 *
 * NOTE: this will *add* functions to *from*
 */
function delegate(from, to) {
    _.each(_.filter(_.functionsIn(to), ( name ) => {
        return from[name] === undefined;
    }), ( name ) => {
        from[name] = function() { return to[name].apply(to, arguments); }.bind(from);
    });
}

exports.delegate = delegate;
