"use strict";

class TypeError extends Error {}

function ParameterError(/* Object */ actualObject, /* string */ parameterName, /* string */ shouldBeTypeName) {
    this.message = [parameterName, 'should be of type', shouldBeTypeName, ':', actualObject.constructor.name, actualObject].join(' ');
}

ParameterError.prototype = new Error();
ParameterError.prototype.constructor = ParameterError;

exports.TypeError = TypeError;
exports.ParameterError = ParameterError;
