"use strict";

const entity_Entity = require('entity/Entity.js');
const app_customer_CustomerEntityModel = require('app/customer/CustomerEntityModel.js');
const sql_DB = require('sql/DB.js');

function CustomerEntity() {
    entity_Entity.Entity.call(this);
}

function create(db) {
    console.log('customerentity create arguments', arguments);
    const entityModel = app_customer_CustomerEntityModel.create(db);

    const cen = new CustomerEntity();
    cen.setModel(entityModel);
    return cen;
}

CustomerEntity.prototype = new entity_Entity.Entity();
CustomerEntity.prototype.constructor = CustomerEntity;

exports.create = create;
exports.CustomerEntity = CustomerEntity;
