"use strict";

const BJOO = require('BJOObject.js');
const DelegationUtil = require('DelegationUtil.js');
const app_CustomerTable = require('app/customer/CustomerTable.js');
const model_EntityModel = require('model/EntityModel.js');
const entity_Entity = require('entity/Entity.js');

function CustomerEntity() {
    entity_Entity.Entity.apply(this);
}

function create(db) {
    const table = app_CustomerTable.create();
    const entityModel = model_EntityModel.create(db, table);
    const customerEntity = new CustomerEntity();
    customerEntity.setModel(entityModel);

    return customerEntity;
}

function createFromModel(entityModel) {
    const customerEntity = new CustomerEntity();
    customerEntity.setModel(entityModel);
    return customerEntity;
};


exports.CustomerEntity = CustomerEntity;
exports.create = create;
exports.createFromModel = createFromModel;

CustomerEntity.prototype = new entity_Entity.Entity();
CustomerEntity.prototype.constructor = CustomerEntity;

