"use strict";

const model_EntityModel = require('model/EntityModel.js');
const entity_Entity = require('entity/Entity.js');
const sql_Table = require('sql/Table.js');
const sql_Field = require('sql/Field.js');
const sql_ValueField = require('sql/ValueField.js');
const sql_DB = require('sql/DB.js');

function CustomerEntityModel() {
    model_EntityModel.EntityModel.call(this);
}

function create(db) {
    console.log('customerentitymodel create arguments', arguments);
    const table = sql_Table.create('customer'); // TODO move this to app...CustomerTable.js
    table.addField(sql_ValueField.create('id', sql_Field.DataType.int));
    table.addField(sql_ValueField.create('name', sql_Field.DataType.string));
    const cem = new CustomerEntityModel();
    cem.setDb(db);
    cem.setTable(table);
    return cem;
}

CustomerEntityModel.prototype = new model_EntityModel.EntityModel();
CustomerEntityModel.prototype.constructor = CustomerEntityModel;

exports.create = create;
exports.CustomerEntityModel = CustomerEntityModel;

