"use strict";

const entity_Entity = require('entity/Entity.js');
const entity_EntitySet = require('entity/EntitySet.js');
const app_customer_CustomerEntity = require('app/customer/CustomerEntity.js');
const app_customer_CustomerEntityModel = require('app/customer/CustomerEntityModel.js');
const app_customer_CustomerEntitySetModel = require('app/customer/CustomerEntitySetModel.js');
const sql_DB = require('sql/DB.js');

function CustomerEntitySet() {
    entity_EntitySet.EntitySet.call(this);
}

function create(db) {
    const entitySetModel = app_customer_CustomerEntitySetModel.create(db);

    const ces = new CustomerEntitySet();
    ces.setModel(entitySetModel);
    ces.setEntityConstructor(app_customer_CustomerEntity.create);
    return ces;
}

CustomerEntitySet.prototype = new entity_EntitySet.EntitySet();
CustomerEntitySet.prototype.constructor = CustomerEntitySet;
CustomerEntitySet.prototype._model = null;

exports.create = create;
exports.CustomerEntitySet = CustomerEntitySet;

