"use strict";

const sql_Table = require('sql/Table.js');
const sql_Field = require('sql/Field.js');
const sql_ValueField = require('sql/ValueField.js');

function CustomerTable() {
    /* @DEVELOPMENT { */
        if ( arguments.length !== 0 ) { throw new Error('Illegal argument length '  + arguments.length + ', should be 0'); }
    /* @DEVELOMPENT } */

    sql_Table.Table.apply(this);
    this.setName('customer');
    this.addField(sql_ValueField.create('id', sql_Field.DataType.int));
    this.addField(sql_ValueField.create('name', sql_Field.DataType.string));
}

function create() {
    return new CustomerTable();
}

exports.CustomerTable = CustomerTable;
exports.create = create;

CustomerTable.prototype = new sql_Table.Table();
CustomerTable.prototype.constructor = CustomerTable;
