/**
 *  a concrete bo for the customer table
 *  that knows how to set its own dao properly
 */

"use strict";

var util = require('util');
var m_bo_primaryBo = require('bo/primaryBo.js');
var m_app_customer_customerDao = require('app/customer/customerDao.js');
var m_sql_db = require('sql/db.js');

function CustomerBo(/* m_sql_db.DB */ p_db) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT { */
            var ARGLEN = 1; if ( arguments.length !== ARGLEN ) { throw new Error('Illegal argument length '  + arguments.length + ', should be ' + ARGLEN); }
            var PARAM = p_db; if ( !(PARAM instanceof m_sql_db.DB) ) { throw new Error('typecheck failed on ' + PARAM + ': ' + PARAM); }
        /* @DEVELOMPENT } */
        m_bo_primaryBo.PrimaryBo.call(this, p_db);
        this.dao(m_app_customer_customerDao.customerDao(p_db));
    } else {
        m_bo_primaryBo.PrimaryBo.call(this);
    }
    this._className = 'app.CustomerBo';
};

function customerBo(/* m_sql_db.DB */ p_db) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT { */
            var ARGLEN = 1; if ( arguments.length !== ARGLEN ) { throw new Error('Illegal argument length '  + arguments.length + ', should be ' + ARGLEN); }
            var PARAM = p_db; if ( !(PARAM instanceof m_sql_db.DB) ) { throw new Error('typecheck failed on ' + PARAM + ': ' + PARAM); }
        /* @DEVELOMPENT } */
        return new CustomerBo(p_db);
    } else {
        return new CustomerBo(); 
    }
}

CustomerBo.prototype = new m_bo_primaryBo.PrimaryBo();
CustomerBo.prototype.constructor = CustomerBo;

exports.customerBo = customerBo;
exports.CustomerBo = CustomerBo;
