var util = require('util');
var m_bo = require('bo/bo.js');
var m_app_customer_customerDao = require('app/customer/CustomerDao.js');

function CustomerBo(p_db) {
    m_bo.BO.call(this, p_db, m_app_customer_customerDao.customerDao(p_db));
    this._className = 'customerBo.CustomerBo';
};

CustomerBo.prototype = new m_bo.BO();

////////////////////////////////////////////////////

CustomerBo.prototype.loadById = function(id, callback) {
    this._dao.loadById(id, function(err, dao) {
        if (err) callback(err);
        this._fieldValuesFromDao(dao);
        callback(false, this);
    }.bind(this));
};

function customerBo(p_db) { return new CustomerBo(p_db); }

exports.customerBo = customerBo;
exports.CustomerBo = CustomerBo;
