"use strict";

var util = require('util');
var m_bo_primaryBo = require('bo/primaryBo.js');
var m_app_customer_customerDao = require('app/customer/CustomerDao.js');

function CustomerBo(/*db_DB*/p_db) {
    if ( arguments.length > 0 ) {
        m_bo_primaryBo.PrimaryBo.call(this, m_app_customer_customerDao.customerDao(p_db));
    } else {
        m_bo_primaryBo.PrimaryBo.call(this);
    }
    this._className = 'app.CustomerBo';
};

CustomerBo.prototype = new m_bo_primaryBo.PrimaryBo();
CustomerBo.prototype.constructor = CustomerBo;

function customerBo(p_db) { if (arguments.length > 0 ) { return new CustomerBo(p_db); } else { return new CustomerBo(); } }

exports.customerBo = customerBo;
exports.CustomerBo = CustomerBo;
