"use strict";

var m_primaryDao = require('dao/primaryDao.js');
var db = require('sql/sqlite/db.js');
var table = require('sql/table.js');
var field = require('sql/field.js');

function CustomerDao(p_db) {
    m_primaryDao.PrimaryDao.call(this, p_db);
    this._table 
        .name('customer')
        .field(field.field('firstName', field.Type.string))
        .field(field.field('lastName', field.Type.string));
}

CustomerDao.prototype = new m_primaryDao.PrimaryDao();


function customerDao(p_db) { return new CustomerDao(p_db); }

exports.customerDao = customerDao;
exports.CustomerDao = CustomerDao;
