"use strict";

var m_dao_primaryDao = require('dao/primaryDao.js');
var m_sql_table = require('sql/table.js');
var m_sql_field = require('sql/field.js');
var m_sql_fieldLink = require('sql/fieldLink.js');

function CustomerDao(p_db) {
    var table = m_sql_table.table('customer')
        .field(m_sql_field.field('id'), m_sql_field.DataType.int)
        .field(m_sql_field.field('firstName', m_sql_field.DataType.string))
        .field(m_sql_field.field('lastName', m_sql_field.DataType.string));
    var invTable = m_sql_table.table('invoice')
        .field(m_sql_field.field('id'), m_sql_field.DataType.int)
        .field(m_sql_field.field('customerId'), m_sql_field.DataType.int)
        .field(m_sql_field.field('amount'), m_sql_field.DataType.int) // TODO
    this.fieldLinks(m_sql_fieldLink.fieldLink(table.field('id'), invTable.field('customerId'), m_sql_fieldLink.Type.manyToOne));
    m_dao_primaryDao.PrimaryDao.call(this, p_db, table);
}

CustomerDao.prototype = new m_dao_primaryDao.PrimaryDao();


function customerDao(p_db) { return new CustomerDao(p_db); }

exports.customerDao = customerDao;
exports.CustomerDao = CustomerDao;
