"use strict";

var util = require('util');
var m_bo_primaryBo = require('bo/primaryBo.js');
var m_app_invoice_invoiceDao = require('app/invoice/invoiceDao.js');
var m_app_invoice_invoiceBo = require('app/invoice/invoiceDao.js');

function InvoiceBo(p_db) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT { */ 
            var m_sql_db = require('sql/db.js');
            if ( arguments.length !== 1 ) { throw new Error('Illegal argument length '  + arguments.length + ', should be 1'); }
        /* @DEVELOMPENT } */
        m_bo_primaryBo.PrimaryBo.call(this, p_db);
        this.dao(m_app_invoice_invoiceDao.invoiceDao(p_db));
    } else {
        m_bo_primaryBo.PrimaryBo.call(this);
    }
    this._className = 'app.InvoiceBo';
};

InvoiceBo.prototype = new m_bo_primaryBo.PrimaryBo();
InvoiceBo.prototype.constructor = InvoiceBo;

function invoiceBo(p_db) { if ( arguments.length > 0 ) { return new InvoiceBo(p_db); } else { return new InvoiceBo(); } }

exports.InvoiceBo = InvoiceBo;
exports.invoiceBo = invoiceBo;
