const app_customer_CustomerEntitySet = require('app/customer/CustomerEntitySet.js');
const web_server_controller_DefaultController = require('web/server/controller/DefaultController.js');
const sql_DB = require('sql/DB.js');

/**
 * @class app.web.server.CustomerController
 * @constructor
 * @param {Object} config
 */
function CustomerController(config) {
    web_server_controller_DefaultController.DefaultController.call(this, config);
    const db = sql_DB.create(config.db.url).open(config.db.url);
    this._entitySet = app_customer_CustomerEntitySet.create(db);
}

/**
 * external constructor
 *
 * @param {Object} config
 */
function create(config) {
    return new CustomerController(config);
}

CustomerController.prototype = new web_server_controller_DefaultController.DefaultController();
CustomerController.prototype.constructor = CustomerController;

exports.CustomerController = CustomerController;
exports.create = create;

CustomerController.prototype.listAction = function(request, response) {
    return web_server_controller_DefaultController.DefaultController.prototype.listAction.call(this, this._entitySet, request, response); // TODO okay this is ridonculous!!
};
