/**
 * this file defines a primary dao, that is, a dao that has an id field
 */

var m_dao = require('dao/dao.js');
var condition = require('sql/condition.js');
var table = require('sql/table.js');
var field = require('sql/field.js');
var query = require('sql/query.js');

function PrimaryDao(p_db) {
    m_dao.DAO.call(this, p_db, 
        table.table('primary')
            .field(field.field('id', field.Type.int))
    );
}

PrimaryDao.prototype = new m_dao.DAO();


PrimaryDao.prototype.loadById = function(id, callback) {
    this.field('id').value(id);
    var q = this._createIdSelect();
    this._db.fetchRow(q, [], function(err, row) { 
        if ( err ) {
            return callback(err);
        }
        callback(false, row);
    });
};

PrimaryDao.prototype.save = function(callback) {
    var q = null;
    if ( typeof(this.fieldValue('id')) !== 'undefined' ) {
        q = query.update()
            .table(this._table)
            .fields(this._fieldsAsList())
            .where(this._createIdWhere());
    } else {
        q = query.insert()
            .into(this._table)
            .fields(this._fieldsAsList());
    }
    this._db.run(q, function(err) {
        if ( err ) {
            throw err;
        }
        callback();
    });
};

PrimaryDao.prototype._createIdSelect = function() {
    return query.select()
        .fields(this._fieldsAsList())
        .from(this._table)
        .where(this._createIdWhere());
};

PrimaryDao.prototype._createIdWhere = function() {
    return condition.condition(this._fields['id'], condition.Op.eq, this._fields['id'].value());
};

function primaryDao(p_db) { return new PrimaryDao(p_db); }

exports.PrimaryDao = PrimaryDao;
exports.primaryDao = primaryDao;
