/**
 * an EntitySet is an object that is fit for its corresponding entry in the database
 * ... apart from that, nothing is known about the entity, so for saving it, we shall 
 * have to use all primary key fields
 *
 * TODO: filename is lowercase now, and damn windoze!!
 */

"use strict";

const _ = require('lodash');
const q = require('q');
const BJOObject = require('BJOObject.js');
const model_EntityModel = require('model/EntityModel.js');
const model_EntitySetModel = require('model/EntitySetModel.js');

function EntitySet() {
    BJOObject.BJOObject.call(this);
}

function create(/* model_EntitySetModel.EntitySetModel */ model) {
    console.log('EntitySet creaet');
    const set = new EntitySet();
    if ( arguments.length > 0 ) {
        /* @DEVELOMPENT { */
            if ( !(model instanceof model_EntitySetModel.EntitySetModel) ) { throw new Error('model must be of type model_EntitySetModel.EntitySetModel'); }
        /* @DEVELOMPENT } */
        set.setModel(model);
    }
    return set;
}

EntitySet.prototype = BJOObject.create();
EntitySet.prototype.constructor = EntitySet;
EntitySet.prototype._model = null;

exports.EntitySet = EntitySet;
exports.create = create;

EntitySet.prototype.setModel = function(/* model_EntitySetModel.EntitySetModel */ model) {
    /* @DEVELOMPENT { */
        if ( !(model instanceof model_EntitySetModel.EntitySetModel) ) { throw new Error('model must be of type model_EntitySetModel.EntitySetModel'); }
    /* @DEVELOMPENT } */
    this._model = model;
};

EntitySet.prototype.loadEntityById = function(/* int */ id) { console.log('EntitySet loadEntityById'); return this._model.loadEntityById(id); }
EntitySet.prototype.findEntityById = function(/* int */ id) { return this._model.findEntityById(id); }
EntitySet.prototype.findAllEntities = function() { return this._model.findAllEntities(); }
EntitySet.prototype.findEntities = function(/* sql_ConditionSet */ conditionSet) { return this._model.findEntities(conditionSet); }
