"use strict";

const _ = require('lodash');
const model_Model = require('model/Model.js');
const DelegationUtil = require('DelegationUtil.js');

function EntityModel(/* Object */ object) {
    this._model = model_Model.create(object);
    DelegationUtil.delegate(this, this._model);
}

function create(/* Object */ object) {
    let model = new EntityModel(object);
    return model;
}

EntityModel.prototype = {};
EntityModel.prototype.constructor = EntityModel;

exports.EntityModel = EntityModel;
exports.create = create;

EntityModel.prototype.save = function() {
};

EntityModel.prototype._insert = function() {
};

EntityModel.prototype._update = function() {
};
