var m_customerBo = require('app/customer/customerBo.js');
var m_controller = require('server/defaultController.js');

function edit(request, response, responseCallback) {
    var customerBo = m_customerBo.customerBo(request.db);
    return m_controller.edit(customerBo, request, response, responseCallback);
}

function list(request, response, responseCallback) {
    var customerBo = m_customerBo.customerBo(request.db);
    return m_controller.list(customerBo, request, response, responseCallback);
}

function save(request, response, responseCallback) {
    var customerBo = m_customerBo.customerBo(request.db);
    return m_controller.save(customerBo, request, response, responseCallback);
}

exports.edit = edit;
exports.list = list;
exports.save = save;
