var m_customerBo = require('app/customer/customerBo.js');
function edit(request, response, responseCallback) {
    console.log('edit req', request);
    var customerBo = m_customerBo.customerBo(request.db);
    customerBo.loadById(request.query.id, function(err, customer) {
        var data = { action: 'edit', 
            row: [ 
                customer.field('id').webize(),
                customer.field('firstName').webize(),
                customer.field('lastName').webize(),
            ],
        };
        console.log('### data', data);
        response.data = data;
        responseCallback(response);
    });
}

function save(request, response, responseCallback) {
    var customerBo = m_customerBo.customerBo(request.db);
    customerBo.firstName(request.query.firstName);
    customerBo.lastName(request.query.lastName);
    if ( typeof(request.query.id) !== 'undefined' ) {
        customerBo.id(request.query.id);
    }
    customerBo.save(function(err, res) {
        response.data = { id: customerBo.id() };
        responseCallback(response);
    });
}

exports.edit = edit;
exports.save = save;
