function edit(p_db, request, response, responseCallback) {
    var m_customerBo = require('app/customer/customerBo.js');
    var customerBo = m_customerBo.customerBo(p_db);
    customerBo.loadById(request.query.id, function(err, customer) {
        var data = { action: 'edit', 
            row: [ 
                customer.field('firstName').uncircularize(),
                customer.field('lastName').uncircularize(),
            ],
        };
        console.log('### data', data);
        response.text = JSON.stringify(data);
        responseCallback(response);
    });
}

exports.edit = edit;
