function error(request, response, responseCallback) {
    response.returnCode = 404;
    response.text = 'uninternal server error';
    responseCallback(response);
}

function errorWithText(request, response, responseCallback) {
    response.returnCode = 404;
    response.text = request.text;
    responseCallback(response);
}


exports.error = error;
exports.errorWithText = errorWithText;
