var m_invoiceBo = require('app/invoice/invoiceBo.js');
var m_dao_daoSet = require('dao/daoSet.js');
var m_bo_boSet = require('bo/boSet.js');
var m_controller = require('server/defaultController.js');
var m_app_invoice_invoiceDao = require('app/invoice/invoiceDao.js');
var m_app_invoice_invoiceBo = require('app/invoice/invoiceBo.js');

function edit(request, response, responseCallback) {
    var invoiceDao = m_app_invoice_invoiceDao.invoiceDao(request.db);
    var invoiceBo = m_invoiceBo.invoiceBo(invoiceDao);
    return m_controller.edit(invoiceBo, request, response, responseCallback);
}

function list(request, response, responseCallback) {
    var invoiceDao = m_app_invoice_invoiceDao.invoiceDao(request.db);
    var daoSet = m_dao_daoSet.daoSet(request.db, invoiceDao.table(), m_app_invoice_invoiceDao.invoiceDao);
    var boSet = m_bo_boSet.boSet(daoSet, m_app_invoice_invoiceBo.invoiceBo);
    return m_controller.list(boSet, request, response, responseCallback);
}

function saveRow(request, response, responseCallback) {
    var invoiceDao = m_app_invoice_invoiceDao.invoiceDao(request.db);
    var invoiceBo = m_invoiceBo.invoiceBo(invoiceDao);
    return m_controller.saveRow(invoiceBo, request, response, responseCallback);
}

function saveField(request, response, responseCallback) {
    var invoiceDao = m_app_invoice_invoiceDao.invoiceDao(request.db);
    var invoiceBo = m_invoiceBo.invoiceBo(invoiceDao);
    return m_controller.saveField(invoiceBo, request, response, responseCallback);
}

function count(request, response, responseCallback) {
    var invoiceDao = m_app_invoice_invoiceDao.invoiceDao(request.db);
    var invoiceBo = m_invoiceBo.invoiceBo(invoiceDao);
    return m_controller.count(invoiceBo, request, response, responseCallback);
}

exports.edit = edit;
exports.list = list;
exports.saveField = saveField;
exports.saveRow = saveRow;
exports.count = count;
