/**
 * this file defines a master dao, that is, a dao that has an id field
 */

var dao = require('./dao.js');
var query = require('server/sql/query.js');
var condition = require('server/sql/condition.js');

function MasterDao(db, table) {

}

MasterDao.prototype = new dao.DAO();

function masterDao(db, table) { return new MasterDao(db, table); }

MasterDao.prototype.id = function(id) {
    if ( typeof(id) !== 'undefined' ) {
        this.field('id').value(id);
        return this;
    }
    return this.field('id').value();
};

MasterDao.prototype.loadById = function(id, callback) {
    this.field('id').value(id);
    var query = this._createIdSelect();
    this._db.fetchRow(query, [], function(err, row) { 
        if ( err ) {
            return callback(err);
        }
        callback(false, row);
    });
};

MasterDao.prototype._createIdSelect = function() {
    return query.select()
        .fields(this._fieldsAsList())
        .from(this._table)
        .where(
            condition.condition(this._fields['id'], condition.Op.eq, this._fields['id'].value()));
};

exports.MasterDao = MasterDao;
exports.masterDao = masterDao;
