var bjoo = require('BJOObject.js');

var Type = { sum: 'sum', count: 'count' };

function Aggregate(type, field) {
    this._className = 'sql.Aggregate';
    this._type = type;
    this._field = field;
}

Aggregate.prototype = new bjoo.BJOObject();

Aggregate.prototype.type = function(type) {
    if ( typeof(type) !== 'undefined' ) {
        this._type = type;
        return this;
    }
    return this._type;
};

Aggregate.prototype.field = function(field) {
    if ( typeof(field) !== 'undefined' ) {
        this._field = field;
        return this;
    }
    return this._field;
};

function sum(field) { return new Aggregate(Type.sum, field); }
function count(field) { return new Aggregate(Type.count, field); }
function aggregate(type, field) { return new Aggregate(type, field); }

exports.Type = Type;
exports.Aggregate = Aggregate;
exports.aggregate = aggregate;
exports.sum = sum;
exports.count = count;
