var bjoo = require('BJOObject.js');

var Type = { create: 'create' };

function Query(type) {
    bjoo.BJOObject.apply(this);
    this._className = 'sql.ddl.Query';
    this._type = type;
    this._table = null;
    this._field = null;
}

Query.prototype.type = function() {
    return this._type;
};
Query.prototype.table = function(table) {
    if ( typeof(table) !== 'undefined' ) {
        this._table = table;
        return this;
    }
    return this._table;
};

Query.prototype.field = function(field) {
    if ( typeof(field) !== 'undefined' ) {
        this._field = field;
        return this;
    }
    return this._field;
};

function query(type) { return new Query(type); }
function create(table) { return new Query(Type.create).table(table); }

exports.Type = Type;
exports.Query = Query;
exports.query = query;
exports.create = create;
