/**
 */

"use strict";

const _ = require('lodash');
const q = require('q');

const model_EntitySetModel = require('model/EntitySetModel.js');
const sql_Query = require('sql/Query.js');
const sql_Field = require('sql/Field.js');
const sql_Table = require('sql/Table.js');
const sql_ConditionSet = require('sql/ConditionSet.js');
const sql_Aggregate = require('sql/Aggregate.js');
const sql_Filter = require('sql/Filter.js');

function CountField() {
    sql_Field.Field.call(this);
}

/**
 */
function create(/* String */ name, /* sql_Field.Field */ idField, /* model_EntitySetModel.EntitySetModel */ entitySetModel, /* sql_Field.Field */ referencedIdField, /* String */ label) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT { */
            assert.strictEqual(arguments.length, 5, 'arguments.length must be 5');
        /* @DEVELOMPENT } */
        const field = new CountField();
        field.setName(name);
        field.setDataType(sql_Field.DataType.int); // ??? TODO ??? 
        field.setIdField(idField);
        field.setEntitySetModel(entitySetModel);
        field.setReferencedIdField(referencedIdField);
        field.setLabel(label);
        return field;
    }  else {
        return new CountField();
    }
}

exports.CountField = CountField;
exports.create = create;

CountField.prototype = new sql_Field.Field();
CountField.prototype.constructor = CountField;

CountField.prototype.clone = function() {
    return create(this._name, this._idField, this._entitySetModel, this._referencedIdField, this._label); 
};

/**
 * getter/setter
 */

CountField.prototype.getIdField = function() {
    return this._idField;
};
CountField.prototype.setIdField = function(/* sql_LookupIdField */ idField) {
    this._idField = idField;
    return this;
};

CountField.prototype.getEntitySetModel = function() {
    return this._entitySetModel;
};
CountField.prototype.setEntitySetModel = function(/* model_EntitySetModel.EntitySetModel */ entitySetModel) {
    this._entitySetModel = entitySetModel;
    return this;
};

CountField.prototype.getReferencedIdField = function() {
    return this._referencedIdField;
};
CountField.prototype.setReferencedIdField = function(/* sql_Field */ referencedIdField) {
    this._referencedIdField = referencedIdField;
    return this;
};

/**
 * @return [ promise(entity) ]
 */
CountField.prototype.getValue = function() {
    return this._idField.getValue().then( (id) => {
        if ( !id ) { throw new Error('no id for count field ' + this._name + ' ' + this._idField.getName()); }

        const cond = sql_Filter.create()
            .setField(this._referencedIdField)
            .setOp(sql_Filter.Op.eq)
            .setCompareTo(id);
        const query = sql_Query.select()
            .setFields([ sql_Aggregate.count() ])
            .from(this._referencedIdField.getTable())
            .where(cond);

        return this._entitySetModel.getDb().fetchRow(query).then( ( row ) => {
            return q(row['aggregate']); // TODO name of field is... duh
        });
    });
};

CountField.prototype.updateReferences = function() {
    this.setIdField(this._table.getField(this._idField.getName()));
};
