/**
 * this is a lookup field onto another entity's id
 * note that we cannot lookup onto any other field
 * we shall probably create a better version of this later on...
 */

"use strict";

var _ = require('lodash');

var util = require('util');
var Util = require('util.js');
var model_EntitySetModel = require('model/EntitySetModel.js');
var sql_Field = require('sql/Field.js');
var sql_Query = require('sql/Query.js');

function LookupIdField() {
    sql_Field.Field.call(this);
}

/**
 * @param name the name of this field
 * @param masterField the field we are linked to
 */
function create(/* String */ name) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT { */
        /* @DEVELOMPENT } */
        const field = new LookupIdField();
        field.setName(name);
        field.setDataType(sql_Field.DataType.int);
        return field;
    }  else {
        return new LookupIdField();
    }
}

exports.LookupIdField = LookupIdField;
exports.create = create;

LookupIdField.prototype = new sql_Field.Field();
LookupIdField.prototype.constructor = LookupIdField;

LookupIdField.prototype.clone = function() {
    throw 'not yet implemented';
};
