"use strict";

var _ = require('lodash');
var bjoo = require('BJOObject.js');

const sql_Field = require('sql/Field.js');
const DelegationUtil = require('DelegationUtil.js');

function ValueField(/* sql_Field */ field, value) {
    if ( arguments.length > 0 ) {
        this._field = field;
        DelegationUtil.delegate(this, this._field);
        this._value = value;
    }
}

function create(field, value) {
    if ( arguments.length > 0 ) { return new ValueField(field, value); } else { return new ValueField(); }
}

ValueField.prototype = new bjoo.BJOObject();
ValueField.prototype.constructor = ValueField;

ValueField.prototype.getValue = function() {
    return this._value;
};
ValueField.prototype.setValue = function(value) {
    this._value = value;
    return this;
};

exports.ValueField = ValueField;
exports.create = create;
