/**
 * A LazyFieldLink is a prototype of a fieldLink, that will only create the actual link when its apply() is called
 * - so we can create fieldLinks before we actually have all the fields
 * - this is the lazy variant for creating a m_sql_fieldLink.fieldLink
 */

"use strict";

var _ = require('underscore');
var m_sql_table = require('sql/table.js');
var m_bjoo = require('BJOObject.js');
var m_util = require('util.js');
var m_sql_baseLazyFieldLink = require('sql/baseLazyFieldLink.js');
var m_sql_fieldLink = require('sql/fieldLink.js');

function LazyFieldLink(/* m_sql_table.Table */ table, /* string */ fieldName, /* m_sql_table.Table */ sourceTable, /* string */ sourceFieldName, /* m_sql_table.Table */ targetTable, /* string */ targetFieldName, /* m_sql_fieldLink.Type */ type) {
    m_sql_baseLazyFieldLink.BaseLazyFieldLink.call(this, table, fieldName);
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT */ 
        if ( arguments.length !== 7 ) { throw new Error('illegal argument length ' + arguments.length); }
            if ( !(sourceTable instanceof m_sql_table.Table) ) { throw new Error('Typecheck failure: sourceTable should be of class m_sql_table.Table: ' + sourceTable); }
            if ( !(targetTable instanceof m_sql_table.Table) ) { throw new Error('Typecheck failure: targetTable should be of class m_sql_table.Table: ' + targeTable); }
            if ( !( m_util.isString(sourceFieldName) ) ) { throw new Error('typecheck failed on sourceFieldName: ' + sourceFieldName + ', must be string'); }
            if ( !( m_util.isString(targetFieldName) ) ) { throw new Error('typecheck failed on targetFieldName: ' + targetFieldName + ', must be string'); }
        /* @DEVELOMPENT } */

        this._sourceTable = sourceTable;
        this._sourceFieldName = sourceFieldName;
        this._targetTable = targetTable;
        this._targetFieldName = targetFieldName;
        this._type = type;
    } 
}

function lazyFieldLink(/* m_sql_table.Table */ table, /* string */ fieldName, /* m_sql_table.Table */ sourceTable, /* string */ sourceFieldName, /* m_sql_table.Table */ targetTable, /* string */ targetFieldName, /* m_sql_fieldLink.Type */ type) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT */ 
            if ( arguments.length !== 7 ) { throw new Error('illegal argument length ' + arguments.length); }
            if ( !(sourceTable instanceof m_sql_table.Table) ) { throw new Error('Typecheck failure: sourceTable should be of class m_sql_table.Table: ' + sourceTable); }
            if ( !(targetTable instanceof m_sql_table.Table) ) { throw new Error('Typecheck failure: targetTable should be of class m_sql_table.Table: ' + targeTable); }
            if ( !( m_util.isString(sourceFieldName) ) ) { throw new Error('typecheck failed on sourceFieldName: ' + sourceFieldName + ', must be string'); }
            if ( !( m_util.isString(targetFieldName) ) ) { throw new Error('typecheck failed on targetFieldName: ' + targetFieldName + ', must be string'); }

            return new LazyFieldLink(table, fieldName, sourceTable, sourceFieldName, targetTable, targetFieldName, type);
        /* @DEVELOMPENT } */
    }  else {
        return new LazyFieldLink();
    }
}

LazyFieldLink.prototype = new m_sql_baseLazyFieldLink.BaseLazyFieldLink();
LazyFieldLink.prototype.constructor = LazyFieldLink;

//======================================

/**
 * create a link from the lazyFieldLink, and apply this link to the source table/field
 */
LazyFieldLink.prototype.apply = function() {
    var sourceField = this._sourceTable.field(this._sourceFieldName);
    var targetField = this._targetTable.field(this._targetFieldName);
    var link = m_sql_fieldLink.fieldLink(sourceField, targetField, this._type);
    this._table.field(this._fieldName).link(link);
};

exports.LazyFieldLink = LazyFieldLink;
exports.lazyFieldLink = lazyFieldLink;
