"use strict";

const util = require('util');

const EventEmitter = require('events');

function FieldModel(field) {
    this._field = field;

    this.emit('change', this._field.value);
}

///// this is a FRONTEND field, not a db model field!!!
function fieldModel(field) {
    return new FieldModel(field);
}

FieldModel.prototype = {};
FieldModel.prototype.constructor = FieldModel;
util.inherits(FieldModel, EventEmitter);

FieldModel.prototype.getValue = function() {
    return this._field.value;
};

FieldModel.prototype.getDataType = function() {
    return this._field.dataType
};

FieldModel.prototype.setValue = function(value) {
    this._field.value = value;
    this.emit('change', this._field.value);
};

exports.FieldModel = FieldModel;
exports.fieldModel = exports.fromField = fieldModel;

