const server_DefaultController = require('server/DefaultController.js');

/*
 * TODO just a stub, really... this is far from finished for anythin real-worldy...
 */
function byRoute(request, response, responseCallback) {
    response.data = {};
    if ( request.query.action === 'edit' ) {
        response.data.formName = 'editForm';
    } else {
        response.data.formName = 'listForm';
    }
    return responseCallback(response);
}

exports.byRoute = byRoute;
