"use strict";

const deps = {
    q: require('q'),
    _: require('lodash'),
    fs: require('fs'),
    bjoo: require('BJOObject.js'),
};

/**
 * @class web.server.controller.IndexController
 * @construcor
 */
function IndexController(config) {
    deps.bjoo.BJOObject.call(this);
    if ( arguments.length > 0 ) {
        this._config = config;
    }
}

function create(config) {
    return new IndexController(config);
}

IndexController.prototype = new deps.bjoo.BJOObject();
IndexController.prototype.constructor = IndexController;
IndexController.prototype._config = {};

exports.IndexController = IndexController;
exports.create = create;

IndexController.prototype.indexAction = function(request, response) {
    const filePath = this._config.web.server.documentRoot + '/html/index.html';

    return deps.q.nfbind(deps.fs.readFile)(filePath, 'utf8').then( (text) => {
        return deps._.merge(response, { text: text });
    });
};

IndexController.prototype.serveFileAction = function(request, response) {
    const filePath = this._config.web.server.documentRoot + '/' + request.url;

    return deps.q.nfbind(deps.fs.readFile)(filePath, 'utf8').then( (text) => {
        return deps._.merge(response, { text: text });
    });
};
