"use strict";

const q = require('q');
const bjoo = require('BJOObject.js');

/**
 * @class web.server.controller.IndexController
 * @construcor
 */
function IndexController() {
    bjoo.BJOObject.call(this);
}

function create() {
    return new IndexController();
}

IndexController.prototype = new bjoo.BJOObject();
IndexController.prototype.constructor = IndexController;
exports.IndexController = IndexController;
exports.create = create;

IndexController.prototype.indexAction = function(request, response) {
    console.log('IndexController indexAction will return', response);
    return q(response);
};
