/**
 * @module router
 * @function route
 * @param {Object} request
 * @return Object
 */
function route(url) {
    var module = null;
    var subModule = null;
    var action = null;
    var err = false;
    var query = undefined;

    if ( url === '/' ) {
        module = 'index';
        subModule = 'index';
        action = 'index';
    } else if ( url.match(/(\.js|\.css)$/) ) {
        module = 'index';
        subModule = 'index';
        action = 'serveFile';
    } else if ( url.match(/^(\/([\w\-_]+)){1,3}(\?([\w\-%_&=]+))?$/) ) {
        var parts = url.split('?');
        var queryString = null;
        if (parts.length === 2) {
            queryString = parts.pop();
            var qs = require('querystring');
            query = qs.parse(queryString);
        }
        var path = parts[0].split('/');
        module = typeof(path[1]) === 'string' ? path[1] : 'index';
        subModule = typeof(path[2]) === 'string' ? path[2] : 'index';
        action = typeof(path[3]) === 'string' ? path[3] : 'index';
    } else if ( url.match(/\.ico$/) ) {
        console.log('will not route ' + url, 'no error');
        module = 'error';
        subModule = 'error';
        action = 'error';
    } else {
        throw new Error('unable to route');
    }

    return {module: module, subModule: subModule, action: action, query: query};
}

exports.route = route;
